import { LineChart, Line, XAxis, Tooltip, ResponsiveContainer, ReferenceArea } from "recharts";
import "./CardLineChart.css";

function CardLineChart() {
    const data = [
        {
          "name": "L",
          "uv": 4000,
          "pv": 2400,
          "amt": 2400
        },
        {
          "name": "M",
          "uv": 3000,
          "pv": 1398,
          "amt": 2210
        },
        {
          "name": "M",
          "uv": 2000,
          "pv": 9800,
          "amt": 2290
        },
        {
          "name": "J",
          "uv": 2780,
          "pv": 3908,
          "amt": 2000
        },
        {
          "name": "V",
          "uv": 1890,
          "pv": 4800,
          "amt": 2181
        },
        {
          "name": "S",
          "uv": 2390,
          "pv": 3800,
          "amt": 2500
        },
        {
          "name": "D",
          "uv": 3490,
          "pv": 4300,
          "amt": 2100
        }
      ]

    return (
        <section className="cardLineChart">
            <p className="cardLineChart-title">Dur√©e moyenne des sessions</p>
        <ResponsiveContainer width="100%" height={263} wrapperStyle={{ overflow: "hidden" }}>
        <LineChart data={data} outerRadius="75%" margin={{ top: 0, right: 12, bottom: 16, left: 12 }}>
          <defs>
            <linearGradient
              type="basis"
              gradientUnits="userSpaceOnUse"
              id="color"
              x1="-200"
              y1="0"
              x2="500"
              y2="0"
            >
              <stop offset="0%" stopColor="#FF6F6F" />
              <stop offset="100%" stopColor="#FCE5E5" />
            </linearGradient>
          </defs>

          {/* <ReferenceArea
            x1={selectedValue}
            x2={data.sessions.length - 1}
            y1={-20}
            y2={1000}
            fill="rgba(0,0,0,0.1)"
            opacity={1}
            fillOpacity={1}
            ifOverflow="visible"
          /> */}

          <XAxis
            dataKey="name"
            // padding={{ left: 15, right: 15 }}
            axisLine={false}
            tickLine={false}
            tick={{ fill: "#fff", opacity: "0.5", fontSize: 12, fontWeight: 500 }}
          />

          <Line
            type="monotone"
            dataKey="uv"
            margin={{ top: 100 }}
            stroke="url(#color)"
            strokeWidth={3}
            dot={false}
          />
        </LineChart>
      </ResponsiveContainer>
            
      </section>   
    );
}

export default CardLineChart;
