import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, CartesianAxis } from 'recharts';

import blackPoint from '../../assets/black-point.svg'
import redPoint from '../../assets/red-point.svg'
import "./BarsChart.css"

function BarsChart() {
  const data = [];


  return (
    <section className='barsChart'>
      <article className='barsChart-legend'>
        <h2>Activité quotidienne</h2>
        <div className='legend'>
          <p><img src={blackPoint} alt="Black point" />Poids (kg)</p>
          <p><img src={redPoint} alt="Red point" />Calories brûlées (kCal)</p>
        </div>
      </article>
      <ResponsiveContainer width="100%" height={250}>
        <BarChart
          data={[
            { name: 'Lundi', weight: 60, caloriesBurned: 100 },
            { name: 'Mardi', weight: 70, caloriesBurned: 120 },
            { name: 'Mercredi', weight: 65, caloriesBurned: 110 },
            { name: 'Jeudi', weight: 75, caloriesBurned: 130 },
            { name: 'Vendredi', weight: 80, caloriesBurned: 140 },
            { name: 'Samedi', weight: 68, caloriesBurned: 125 },
            { name: 'Dimanche', weight: 72, caloriesBurned: 135 },
          ]}
        >
        <CartesianGrid vertical={false} strokeDasharray="2 2" />
        <CartesianAxis strokeDasharray="0"/>
        <XAxis 
          tick={data.name} 
          tickLine={false} 
          axisLine={false}
        />
        <YAxis 
          dataKey="caloriesBurned" 
          orientation='right' 
          tickCount={3} 
          tickLine={false} 
          axisLine={false} 
          type="number" 
        />
        <Bar 
          dataKey="weight" 
          fill="#282D30" 
          barSize={10} 
          radius={[10, 10, 0, 0]}
        />
        <Bar 
          dataKey="caloriesBurned" 
          fill="#E60000" 
          barSize={10} 
          radius={[10, 10, 0, 0]}
        />
      </BarChart>
      </ResponsiveContainer>

    </section>
  );
}

export default BarsChart;
