import { LineChart, Line, XAxis, Tooltip, ResponsiveContainer, ReferenceArea } from "recharts";
import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import { getSessions } from "../../api";

import "./CardLineChart.css";
const arrayXAxisLabel = ["L", "M", "M", "J", "V", "S", "D"];

function mapData(data) {
  return data?.map((data, index) => {
    data.day = arrayXAxisLabel[index];
    data.idx = index;
    return data;
  });
}

const ActiveDot = (props) => {
  const { cx, cy } = props;

  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      x={cx - 9}
      y={cy - 9}
      width="18"
      height="19"
      viewBox="0 0 18 19"
      fill="none"
    >
      <path
        fillRule="evenodd"
        clipRule="evenodd"
        d="M9 13.8607C11.2091 13.8607 13 12.0809 13 9.88545C13 7.68999 11.2091 5.91022 9 5.91022C6.79086 5.91022 5 7.68999 5 9.88545C5 12.0809 6.79086 13.8607 9 13.8607Z"
        fill="white"
      />
      <path
        d="M9 16.3607C12.5752 16.3607 15.5 13.4762 15.5 9.88545C15.5 6.29466 12.5752 3.41022 9 3.41022C5.42481 3.41022 2.5 6.29466 2.5 9.88545C2.5 13.4762 5.42481 16.3607 9 16.3607Z"
        stroke="white"
        strokeOpacity="0.198345"
        strokeWidth="5"
      />
    </svg>
  );
};


function CardLineChart() {
    

    return (
        <section className="cardLineChart">
            <p className="cardLineChart-title">Dur√©e moyenne des sessions</p>
        <ResponsiveContainer width="100%" height={263} wrapperStyle={{ overflow: "hidden" }}>
        <LineChart data={data} outerRadius="75%">
          <defs>
            <linearGradient
              type="basis"
              gradientUnits="userSpaceOnUse"
              id="color"
              x1="-200"
              y1="0"
              x2="500"
              y2="0"
            >
              <stop offset="0%" stopColor="#FF6F6F" />
              <stop offset="100%" stopColor="#FCE5E5" />
            </linearGradient>
          </defs>

          {/* <ReferenceArea
            x1={selectedValue}
            x2={data.sessions.length - 1}
            y1={-20}
            y2={1000}
            fill="rgba(0,0,0,0.1)"
            opacity={1}
            fillOpacity={1}
            ifOverflow="visible"
          /> */}

          <XAxis
            dataKey="name"
            padding={{ left: 15, right: 15 }}
            axisLine={false}
            tickLine={false}
            tick={{ fill: "#fff", opacity: "0.5", fontSize: 12, fontWeight: 500 }}
          />

          <Line
            type="monotone"
            dataKey="uv"
            margin={{ top: 100 }}
            stroke="url(#color)"
            strokeWidth={3}
            dot={false}
            activeDot={<ActiveDot />}
          />
        </LineChart>
      </ResponsiveContainer>
            
      </section>   
    );
}

export default CardLineChart;
